var TempSpPopup = {
    setup: function() {
      // add hidden 'div' to end of page to display popup:
      let popupDiv = $('<div id="tempSpInfo"></div>');
      popupDiv.hide().appendTo($('body'));
      $(document).on('click', '#temp_sps a', TempSpPopup.getTempSpInfo);
    }
    ,getTempSpInfo: function() {
      $.ajax({type: 'GET',
              url: $(this).attr('href'),
              timeout: 5000,
              success: TempSpPopup.showTempSpInfo,
              error: function(xhrObj, textStatus, exception) { alert('Error!'); }
              // 'success' and 'error' functions will be passed 3 args
             });
      return(false);
    }
    ,showTempSpInfo: function(data, requestStatus, xhrObject) {
      // center a floater 1/2 as wide and 1/4 as tall as screen
      let oneFourth = Math.ceil($(window).width() / 4);
      $('#tempSpInfo').
        css({'left': oneFourth,  'width': 2*oneFourth, 'top': 250}).
        html(data).
        show();
      // make the Close link in the hidden element work
      $('#closeLink').on('click',TempSpPopup.hideTempSpInfo);
      return(false);  // prevent default link action
    }
    ,hideTempSpInfo: function() {
      $('#tempSpInfo').hide();
      return(false);
    }
  };
  $(TempSpPopup.setup);
