var TempSpEditPopup = {

  setup: function() { // add hidden 'div' to end of page to display popup:
    let popupDiv = $('<div id="tempSpEditPopup"></div>');
    popupDiv.hide().appendTo($('body'));
    $(document).on('click', '#temp_sp_edit_popup a', TempSpEditPopup.getTempSpEditPopup);
    },

  getTempSpEditPopup: function() {
    $.ajax({type: 'GET',
            url: $(this).attr('href'),
            timeout: 5000,
            success: TempSpEditPopup.showTempSpEditPopup,
            error: function(xhrObj, textStatus, exception) { alert('Error!'); } // 'success' and 'error' functions will be passed 3 args
            });
    return(false);
    },

  showTempSpEditPopup: function(data, requestStatus, xhrObject) { // center a floater 1/2 as wide and 1/4 as tall as screen
    let oneFourth = Math.ceil($(window).width() / 4);
    $('#tempSpEditPopup').
      css({'left': oneFourth,  'width': 2*oneFourth, 'top': 250}).
      html(data).
      show();
    $('#closeLink').on('click',TempSpEditPopup.hideTempSpEditPopup); // make the Close link in the hidden element work
    return(false);  // prevent default link action
    },

  hideTempSpEditPopup: function() {
    $('#tempSpEditPopup').hide();
    return(false);
    }

  };

$(TempSpEditPopup.setup);
